class Itactica_Intenso_Block_Adminhtml_System_Config_Form_Field_Rating extends Mage_Adminhtml_Block_System_Config_Form_Field
{
    /**
     * Override field method to add js
     *
     * @param Varien_Data_Form_Element_Abstract $element
     * @return String
     */
    protected function _getElementHtml(Varien_Data_Form_Element_Abstract $element)
    {
        // Get the default HTML for this option
        $html = parent::_getElementHtml($element);
 
        $html .= '<br/><br/><div id="itactica-star-rating-preview" style="background-image: url(data:image/svg+xml;base64,); background-size: cover; background-repeat: no-repeat; background-size: 6.75rem; width: 6.75rem; height: 1.25rem">
        <div id="itactica-star-rating-color" style="width:80%; background-size: cover; background-repeat: no-repeat; height: 1.28rem;"></div></div>
        <script>
        var starRatingPreviewModel = Class.create();
        var starColors = {
            turquoise: "data:image/svg+xml;base64,",
            yellow: "data:image/svg+xml;base64,",
            red: "data:image/svg+xml;base64,",
            green: "data:image/svg+xml;base64,",
            blue: "data:image/svg+xml;base64,",
            gray: "data:image/svg+xml;base64,",
            purple: "data:image/svg+xml;base64,",
            pink: "data:image/svg+xml;base64,"
        };

        starRatingPreviewModel.prototype = {
            initialize : function() {
                this.rating = $("'.$element->getHtmlId().'");
                this.previewElement = $("itactica-star-rating-color");
 
                this.refreshPreview();
                this.bindColorChange();
            },
            bindColorChange : function() {
                Event.observe(this.rating, "change", this.refreshPreview.bind(this));

            },
            refreshPreview : function() {
                this.previewElement.setStyle({backgroundImage: "url("+starColors[this.rating.value]+")"});
            }
        }
 
        starRatingFontPreview = new starRatingPreviewModel();
        </script>
        ';
        return $html;
    }
}